    ])

    // 2. Process code blocks with Shiki
    // Find <pre><code class="language-xyz">...</code></pre> and replace with Shiki output
    try {
        const highlighter = await createHighlighter({
            themes: ['one-dark-pro'],
            langs: ['javascript', 'js', 'typescript', 'ts', 'css', 'html', 'json', 'bash', 'shell', 'python', 'java', 'c', 'cpp', 'sql', 'markdown', 'yaml', 'go', 'rust'],
        })

        // Match ALL pre > code blocks to ensure we don't miss any.
        // Updated to handle attributes on <pre> and whitespace (e.g. <pre class="..."> <code>)
        const codeBlockRegex = /<pre[^>]*>\s*<code([^>]*)>([\s\S]*?)<\/code>\s*<\/pre>/g

        const matches: { full: string, attrs: string, code: string, index: number }[] = []
        let match
        while ((match = codeBlockRegex.exec(htmlContent)) !== null) {
            matches.push({ full: match[0], attrs: match[1], code: match[2], index: match.index })
        }

        if (matches.length > 0) {
            let newHtml = ''
            let lastIndex = 0

            for (const m of matches) {
                // Append content before this block
                newHtml += htmlContent.substring(lastIndex, m.index)

                // Extract language from attributes
                // class="language-xyz" or class='language-xyz'
                const langMatch = m.attrs.match(/class=["'](?:[^"']*\s+)?language-([\w-]+)(?:\s+[^"']*)?["']/)
                const lang = langMatch ? langMatch[1] : 'text'

                const rawCode = m.code
                    .replace(/&lt;/g, '<')
                    .replace(/&gt;/g, '>')
                    .replace(/&quot;/g, '"')
                    .replace(/&#39;/g, "'")
                    .replace(/&amp;/g, '&')

                try {
                    const highlighted = highlighter.codeToHtml(rawCode, {
                        lang,
                        theme: 'one-dark-pro',
                    })
                    // Wrap in not-prose to avoid Tailwind Typography overriding styles
                    newHtml += `<div class="not-prose">${highlighted}</div>`
                } catch (e) {
                    console.error('Shiki highlighting failed for lang:', lang, e)
                    // Fallback to plain text highlighting if specific lang fails
                    try {
                        const plain = highlighter.codeToHtml(rawCode, {
                            lang: 'text',
                            theme: 'one-dark-pro',
                        })
                        newHtml += `<div class="not-prose">${plain}</div>`
                    } catch (e2) {
                        // Ultimate fallback: original HTML but ensures dark theme somewhat via parent class
                        newHtml += m.full
                    }
                }

                lastIndex = m.index + m.full.length
            }
            newHtml += htmlContent.substring(lastIndex)
            htmlContent = newHtml
        }
    } catch (e) {
        console.error('Shiki setup failed:', e)
    }

    return (
        <div
            className="prose prose-lg dark:prose-invert max-w-none 
            /* Text content constraint (readability) */
            [&>p]:max-w-3xl [&>p]:mx-auto 
            [&>h1]:max-w-3xl [&>h1]:mx-auto 
            [&>h2]:max-w-3xl [&>h2]:mx-auto 
            [&>h3]:max-w-3xl [&>h3]:mx-auto 
            [&>h4]:max-w-3xl [&>h4]:mx-auto 
            [&>ul]:max-w-3xl [&>ul]:mx-auto 
            [&>ol]:max-w-3xl [&>ol]:mx-auto 
            [&>li]:max-w-3xl [&>li]:mx-auto 
            [&>blockquote]:max-w-3xl [&>blockquote]:mx-auto

            /* Media and Code expansion */
            [&_figure]:my-8 [&_figure]:mx-auto [&_figure]:block [&_figure]:max-w-5xl
            [&_img]:rounded-lg [&_img]:shadow-md [&_img]:w-full
            
            /* Code blocks */
            [&_pre]:!bg-stone-900 [&_pre]:!p-6 [&_pre]:rounded-xl [&_pre]:overflow-x-auto 
            [&_pre]:border [&_pre]:border-white/10 [&_pre]:max-w-5xl [&_pre]:mx-auto [&_pre]:my-8
            
            /* Custom blocks (Excalidraw, Flow, etc) */
            [&>div[data-type]]:max-w-6xl [&>div[data-type]]:mx-auto [&>div[data-type]]:w-full"
            dangerouslySetInnerHTML={{ __html: htmlContent }}
        />
    )
}

'use client'



import { NodeViewWrapper, NodeViewContent } from '@tiptap/react'
import { Info, AlertTriangle, CheckCircle, AlertOctagon } from 'lucide-react'
import { cn } from '@/lib/utils'

const CalloutComponent = ({ node, updateAttributes }: any) => {
    const type = node.attrs.type || 'info'
    const types = {
        info: { icon: Info, color: 'bg-blue-500/10 border-blue-500/50 text-blue-500' },
        warning: { icon: AlertTriangle, color: 'bg-yellow-500/10 border-yellow-500/50 text-yellow-500' },
        success: { icon: CheckCircle, color: 'bg-green-500/10 border-green-500/50 text-green-500' },
        danger: { icon: AlertOctagon, color: 'bg-red-500/10 border-red-500/50 text-red-500' },
    }
    const current = types[type as keyof typeof types] || types.info
    const Icon = current.icon

    return (
        <NodeViewWrapper className={cn("flex gap-3 p-4 rounded-lg border my-4 items-start", current.color)}>
            <div className="mt-1 select-none" contentEditable={false}>
                <Icon size={20} />
            </div>
            <div className="flex-1 min-w-0">
                <div className="font-bold mb-1 select-none cursor-pointer" onClick={() => {
                    const keys = Object.keys(types)
                    const next = keys[(keys.indexOf(type) + 1) % keys.length]
                    updateAttributes({ type: next })
                }}>
                    {type.toUpperCase()}
                </div>
                <NodeViewContent className="outline-none" />
            </div>
        </NodeViewWrapper>
    )
}

export default CalloutComponent

import { useState, useEffect, useCallback, useImperativeHandle, forwardRef } from 'react'
import {
    Heading1, Heading2, Heading3,
    List, ListOrdered,
    MessageSquare, Code,
    Type, CheckSquare
} from 'lucide-react'

export const CommandList = forwardRef((props: any, ref) => {
    const [selectedIndex, setSelectedIndex] = useState(0)

    const selectItem = useCallback(
        (index: number) => {
            const item = props.items[index]

            if (item) {
                // Direct execution to ensure reliability
                item.command({ editor: props.editor, range: props.range })
            }
        },
        [props]
    )

    useEffect(() => {
        setSelectedIndex(0)
    }, [props.items])

    useImperativeHandle(ref, () => ({
        onKeyDown: ({ event }: { event: KeyboardEvent }) => {
            if (event.key === 'ArrowUp') {
                setSelectedIndex((selectedIndex + props.items.length - 1) % props.items.length)
                return true
            }

            if (event.key === 'ArrowDown') {
                setSelectedIndex((selectedIndex + 1) % props.items.length)
                return true
            }

            if (event.key === 'Enter') {
                selectItem(selectedIndex)
                return true
            }

            return false
        },
    }))

    return (
        <div className="z-50 min-w-[200px] overflow-hidden rounded-md border bg-popover p-1 text-popover-foreground shadow-md animate-in fade-in-0 zoom-in-95 data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=closed]:zoom-out-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2">
            {props.items.length ? (
                props.items.map((item: any, index: number) => (
                    <button
                        key={index}
                        type="button"
                        className={`relative flex w-full cursor-pointer select-none items-start rounded-sm px-2 py-1.5 text-sm outline-none transition-colors ${index === selectedIndex ? 'bg-accent text-accent-foreground' : 'hover:bg-accent/50'
                            }`}
                        onMouseDown={(e) => {
                            e.preventDefault() // Prevent focus loss
                            e.stopPropagation()
                            selectItem(index)
                        }}
                    >
                        <div className="flex h-5 w-5 shrink-0 items-center justify-center mr-2 mt-0.5">
                            <item.icon className="h-4 w-4" />
                        </div>
                        <div className="flex flex-col items-start">
                            <span className="font-medium">{item.title}</span>
                            <span className="text-xs text-muted-foreground">{item.description}</span>
                        </div>
                    </button>
                ))
            ) : (
                <div className="p-2 text-sm text-muted-foreground">No result</div>
            )}
        </div>
    )
})

