'use client'

import { memo, useState, useRef, useEffect } from 'react'
import { Handle, Position, NodeResizer, NodeProps, useReactFlow } from '@xyflow/react'
import { Input } from '@/components/ui/input'
import { cn } from '@/lib/utils'

const CustomNode = ({ id, data, selected }: NodeProps) => {
    const { setNodes } = useReactFlow()
    const [isEditing, setIsEditing] = useState(false)
    const [labelText, setLabelText] = useState((data.label as string) || 'Node')
    const inputRef = useRef<HTMLInputElement>(null)

    const bgColor = data.bg || '#ffffff'
    const borderColor = data.borderColor || '#777'
    const textColor = data.color || '#000000'
    const shape = data.shape || 'rounded' // rounded, circle, diamond, parallelogram, database, file

    useEffect(() => {
        setLabelText(data.label as string)
    }, [data.label])

    useEffect(() => {
        if (isEditing) {
            inputRef.current?.focus()
            inputRef.current?.select()
        }
    }, [isEditing])

    const onSubmit = () => {
        setIsEditing(false)
        setNodes((nds) =>
            nds.map((node) => {
                if (node.id === id) {
                    return { ...node, data: { ...node.data, label: labelText } }
                }
                return node
            })
        )
    }

    const onKeyDown = (evt: React.KeyboardEvent) => {
        if (evt.key === 'Enter') {
            onSubmit()
        }
    }

    // Base Styles
    const containerStyle: React.CSSProperties = {
        width: '100%',
        height: '100%',
        minWidth: '100px',
        minHeight: '60px',
        position: 'relative',
    }

    // specific styles for the shape container
    const shapeStyle: React.CSSProperties = {
        backgroundColor: bgColor as string,
        border: `1px solid ${borderColor}`,
        color: textColor as string,
        width: '100%',
        height: '100%',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
    }

    const innerContentStyle: React.CSSProperties = {
        padding: '10px',
        zIndex: 10,
        width: '100%',
    }

    // Logic to modify styles based on shape
    let ShapeRender = (
        <div style={shapeStyle} className="shadow-sm">
            <div style={innerContentStyle}>
                {isEditing ? (
                    <Input
                        ref={inputRef}
                        value={labelText}
                        onChange={(e) => setLabelText(e.target.value)}
                        onBlur={onSubmit}
                        onKeyDown={onKeyDown}
                        className="h-8 text-center bg-transparent border-none shadow-none focus-visible:ring-0 p-0 text-inherit font-inherit w-full"
                    />
                ) : (
                    <span className="pointer-events-none select-none px-2 break-words max-w-full">
                        {labelText}
                    </span>
                )}
            </div>
        </div>
    )

    if (shape === 'circle') {
        shapeStyle.borderRadius = '50%'
    } else if (shape === 'rounded') {
        shapeStyle.borderRadius = '5px'
    } else if (shape === 'diamond') {
        shapeStyle.transform = 'rotate(45deg)'
        shapeStyle.borderRadius = '2px'
        innerContentStyle.transform = 'rotate(-45deg)'
    } else if (shape === 'parallelogram') {
        shapeStyle.transform = 'skew(-20deg)'
        shapeStyle.borderRadius = '2px'
        innerContentStyle.transform = 'skew(20deg)'
    } else if (shape === 'file') {
        // Improved File Shape causing a folded corner look
        // We will render a custom SVG/Clip path combo
        shapeStyle.clipPath = 'polygon(0 0, 85% 0, 100% 15%, 100% 100%, 0 100%)'
        // Add the "fold" using a pseudo-element logic by adding a sibling div
        ShapeRender = (
            <div style={{ ...containerStyle }} className="shadow-sm filter drop-shadow-sm">
                <div style={{ ...shapeStyle, position: 'absolute', top: 0, left: 0 }}>
                    <div style={innerContentStyle}>
                        {isEditing ? (
                            <Input
                                ref={inputRef}
                                value={labelText}
                                onChange={(e) => setLabelText(e.target.value)}
                                onBlur={onSubmit}
                                onKeyDown={onKeyDown}
                                className="h-8 text-center bg-transparent border-none shadow-none focus-visible:ring-0 p-0 text-inherit font-inherit w-full"
                            />
                        ) : (
                            <span className="pointer-events-none select-none px-2 break-words max-w-full">
                                {labelText}
                            </span>
                        )}
                    </div>
                </div>
                {/* The Fold */}
                <div
                    style={{
                        position: 'absolute',
                        top: 0,
                        right: 0,
                        width: '15%',
                        height: '15%', // Rough approximation, heavily depends on aspect ratio. 
                        // Better to use fixed px or calc. Let's assume 100x60 base. 
                        // 15% width is dynamic. 
                        backgroundColor: 'rgba(0,0,0,0.1)',
                        borderLeft: `1px solid ${borderColor}`,
                        borderBottom: `1px solid ${borderColor}`,
                        borderBottomLeftRadius: '2px',
                        background: 'linear-gradient(45deg, transparent 50%, rgba(0,0,0,0.05) 50%)'
                    }}
                />
            </div>
        )
    } else if (shape === 'database') {
        // Improved Database Shape using simpler CSS stacking
        // Base Cylinder
        containerStyle.background = 'transparent';

        ShapeRender = (
            <div style={{ ...containerStyle, display: 'flex', flexDirection: 'column' }} className="group">
                {/* Top Cap */}
                <div style={{
                    height: '20%',
                    width: '100%',
                    border: `1px solid ${borderColor}`,
                    background: bgColor as string,
                    borderRadius: '50%',
                    position: 'absolute',
                    top: 0,
                    zIndex: 2,
                }} />

                {/* Body */}
                <div style={{
                    position: 'absolute',
                    top: '10%', // Halfway down the top cap to blend
                    bottom: 0,
                    width: '100%',
                    border: `1px solid ${borderColor}`,
                    borderTop: 'none',
                    background: bgColor as string,
                    borderBottomLeftRadius: '50% 20%',
                    borderBottomRightRadius: '50% 20%',
                    zIndex: 1,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                }}>
                    <div style={{ ...innerContentStyle, marginTop: '10%' }}>
                        {isEditing ? (
                            <Input
                                ref={inputRef}
                                value={labelText}
                                onChange={(e) => setLabelText(e.target.value)}
                                onBlur={onSubmit}
                                onKeyDown={onKeyDown}
                                className="h-8 text-center bg-transparent border-none shadow-none focus-visible:ring-0 p-0 text-inherit font-inherit w-full"
                            />
                        ) : (
                            <span className="pointer-events-none select-none px-2 break-words max-w-full">
                                {labelText}
                            </span>
                        )}
                    </div>
                </div>
            </div>
        )
    }

    // Handle Styles - Small dots
    const handleStyle = { width: 8, height: 8, background: '#777', zIndex: 50 };

    return (
        <>
            <NodeResizer
                minWidth={50}
                minHeight={50}
                isVisible={selected}
                lineClassName="border-blue-400"
                handleClassName="h-3 w-3 bg-white border-2 border-blue-400 rounded"
            />

            {/* Top Handles */}
            <Handle type="target" position={Position.Top} id="t-in" style={{ ...handleStyle, left: '30%' }} />
            <Handle type="source" position={Position.Top} id="t-out" style={{ ...handleStyle, left: '70%' }} />

            {/* Right Handles */}
            <Handle type="target" position={Position.Right} id="r-in" style={{ ...handleStyle, top: '30%' }} />
            <Handle type="source" position={Position.Right} id="r-out" style={{ ...handleStyle, top: '70%' }} />

            {/* Bottom Handles */}
            <Handle type="target" position={Position.Bottom} id="b-in" style={{ ...handleStyle, left: '30%' }} />
            <Handle type="source" position={Position.Bottom} id="b-out" style={{ ...handleStyle, left: '70%' }} />

            {/* Left Handles */}
            <Handle type="target" position={Position.Left} id="l-in" style={{ ...handleStyle, top: '30%' }} />
            <Handle type="source" position={Position.Left} id="l-out" style={{ ...handleStyle, top: '70%' }} />

            <div
                style={shape === 'file' || shape === 'database' ? { width: '100%', height: '100%' } : shapeStyle}
                onDoubleClick={() => setIsEditing(true)}
                className="shadow-sm hover:shadow-md transition-shadow h-full w-full"
            >
                {shape !== 'file' && shape !== 'database' && (
                    <div style={innerContentStyle} className="w-full h-full flex items-center justify-center">
                        {isEditing ? (
                            <Input
                                ref={inputRef}
                                value={labelText}
                                onChange={(e) => setLabelText(e.target.value)}
                                onBlur={onSubmit}
                                onKeyDown={onKeyDown}
                                className="h-8 text-center bg-transparent border-none shadow-none focus-visible:ring-0 p-0 text-inherit font-inherit w-full"
                            />
                        ) : (
                            <span className="pointer-events-none select-none px-2 break-words max-w-full">
                                {labelText}
                            </span>
                        )}
                    </div>
                )}
                {shape === 'file' && ShapeRender}
                {shape === 'database' && ShapeRender}
            </div>
        </>
    )
}

export default memo(CustomNode)

import { mergeAttributes } from '@tiptap/core'
import { ReactNodeViewRenderer } from '@tiptap/react'
import { ExcalidrawDefinition } from './definitions/Excalidraw'
import ExcalidrawComponent from './ExcalidrawComponent'

export const ExcalidrawExtension = ExcalidrawDefinition.extend({
    addNodeView() {
        return ReactNodeViewRenderer(ExcalidrawComponent)
    },
})

'use client'

import { useCallback, useState, useEffect, useMemo } from 'react'
import {
    ReactFlow,
    MiniMap,
    Controls,
    Background,
    useNodesState,
    useEdgesState,
    addEdge,
    Node,
    Edge,
    Connection,
    ReactFlowProvider,
    useReactFlow,
    ConnectionMode
} from '@xyflow/react'
import '@xyflow/react/dist/style.css'
import { Button } from '@/components/ui/button'
import { Trash, Square, Circle, Diamond, Slash, Database, FileText } from 'lucide-react'
import { NodeViewWrapper } from '@tiptap/react'
import { Card } from '@/components/ui/card'
import CustomNode from './CustomNode'

const initialNodesProp: Node[] = [
    { id: '1', type: 'custom', position: { x: 0, y: 0 }, data: { label: 'Start', bg: '#10b981', shape: 'rounded' } },
];
const initialEdgesProp: Edge[] = [];

const FlowEditor = ({ node, updateAttributes }: any) => {
    // Define custom node types
    const nodeTypes = useMemo(() => ({ custom: CustomNode }), [])

    // Load from attributes
    const [nodes, setNodes, onNodesChange] = useNodesState<Node>(
        node.attrs.nodes?.length ? node.attrs.nodes : initialNodesProp
    );
    const [edges, setEdges, onEdgesChange] = useEdgesState<Edge>(
        node.attrs.edges?.length ? node.attrs.edges : initialEdgesProp
    );

    const [selectedNodeId, setSelectedNodeId] = useState<string | null>(null)
    const [selectedEdgeId, setSelectedEdgeId] = useState<string | null>(null)
    const [bgColor, setBgColor] = useState('#fafafa')
    const [bgVariant, setBgVariant] = useState<any>('dots')

    const onConnect = useCallback(
        (params: Connection) => setEdges((eds) => addEdge({ ...params, type: 'smoothstep', animated: true }, eds)),
        [setEdges],
    );

    const onNodeClick = (_: any, node: Node) => {
        setSelectedNodeId(node.id)
        setSelectedEdgeId(null)
    }

    const onEdgeClick = (_: any, edge: Edge) => {
        setSelectedEdgeId(edge.id)
        setSelectedNodeId(null)
    }

    const onPaneClick = () => {
        setSelectedNodeId(null)
        setSelectedEdgeId(null)
    }

    // Sync back to Tiptap whenever nodes or edges change
    useEffect(() => {
        // Debounce to avoid "flushSync" error and frequent updates
        const timeout = setTimeout(() => {
            updateAttributes({ nodes, edges })
        }, 500)
        return () => clearTimeout(timeout)
    }, [nodes, edges, updateAttributes])

    const addNode = (type: 'rounded' | 'circle' | 'diamond' | 'parallelogram' | 'database' | 'file' = 'rounded') => {
        const id = `node-${Date.now()}`
        const newNode: Node = {
            id,
            type: 'custom',
            // Simple random position around center-ish
            position: { x: Math.random() * 200 + 50, y: Math.random() * 200 + 50 },
            data: {
                label: type === 'diamond' ? 'Wait' : type === 'database' ? 'DB' : 'Node',
                bg: '#ffffff',
                shape: type
            },
        }
        setNodes((nds) => [...nds, newNode])
    }

    const updateNodeColor = (color: string) => {
        if (!selectedNodeId) return
        setNodes((nds) =>
            nds.map((n) => {
                if (n.id === selectedNodeId) {
                    return { ...n, data: { ...n.data, bg: color } }
                }
                return n
            })
        )
    }

    const updateEdgeType = (type: string) => {
        if (!selectedEdgeId) return
        setEdges((eds) =>
            eds.map((e) => {
                if (e.id === selectedEdgeId) return { ...e, type }
                return e
            })
        )
    }

    const updateEdgeAnimated = () => {
        if (!selectedEdgeId) return
        setEdges((eds) =>
            eds.map((e) => {
                if (e.id === selectedEdgeId) return { ...e, animated: !e.animated }
                return e
            })
        )
    }

    const deleteSelected = () => {
        if (selectedNodeId) {
            setNodes((nds) => nds.filter((n) => n.id !== selectedNodeId))
            setSelectedNodeId(null)
        }
        if (selectedEdgeId) {
            setEdges((eds) => eds.filter((e) => e.id !== selectedEdgeId))
            setSelectedEdgeId(null)
        }
    }

    return (
        <NodeViewWrapper className="my-8">
            <Card className="h-[600px] w-full border-2 border-dashed relative overflow-hidden group flex flex-col">
                {/* Toolbar */}
                <div className="p-2 border-b bg-muted/20 flex flex-wrap gap-2 items-center justify-between z-10 min-h-[50px]">
                    <div className="flex gap-2">
                        {/* Shape Adder */}
                        <div className="flex gap-1 border-r pr-2 mr-2">
                            <Button variant="ghost" size="sm" onClick={() => addNode('rounded')} className="h-8 w-8 p-0" title="Box">
                                <Square className="w-4 h-4" />
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => addNode('circle')} className="h-8 w-8 p-0" title="Circle">
                                <Circle className="w-4 h-4" />
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => addNode('diamond')} className="h-8 w-8 p-0" title="Decision">
                                <Diamond className="w-4 h-4" />
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => addNode('parallelogram')} className="h-8 w-8 p-0" title="Input/Output">
                                <Slash className="w-4 h-4 transform -rotate-45" />
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => addNode('database')} className="h-8 w-8 p-0" title="Database">
                                <Database className="w-4 h-4" />
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => addNode('file')} className="h-8 w-8 p-0" title="File">
                                <FileText className="w-4 h-4" />
                            </Button>
                        </div>
                    </div>

                    {/* Contextual Options */}
                    <div className="flex gap-2 items-center">
                        {selectedNodeId && (
                            <div className="flex gap-2 items-center animate-in fade-in">
                                <div className="text-xs text-muted-foreground mr-1">Node:</div>
