                    </p>
                    <input
                        type="file"
                        ref={fileInputRef}
                        className="hidden"
                        accept="image/*"
                        onChange={onUpload}
                    />
                </div>
            </NodeViewWrapper>
        )
    }

    return (
        <NodeViewWrapper className="my-8 group relative leading-none">
            <figure className="relative">
