'use client'

import { Link } from '@/i18n/routingConfig'
import { useTranslations } from 'next-intl'
import Image from 'next/image'
import { Badge } from '@/components/ui/badge'
import { Input } from '@/components/ui/input'
import { Button } from '@/components/ui/button'
import { useEffect, useState } from 'react'
import { getPosts } from '@/app/actions/blog'
import { formatDistance } from 'date-fns'
import { motion, AnimatePresence } from 'framer-motion'
import { Search, X } from 'lucide-react'
import confetti from 'canvas-confetti'

/* Combined type for local + db posts */
type Post = {
    id?: string
    slug: string
    title: string
    excerpt?: string
    date: string | Date
    readTime?: string
    tags?: string[]
    coverImage?: string
}

const container = {
    hidden: { opacity: 0 },
    show: {
        opacity: 1,
        transition: {
            staggerChildren: 0.1
        }
    }
}

import { useGamification } from '@/context/GamificationContext'
import { cn } from '@/lib/utils'

// ... (existing types)

// Typewriter Component (Extracted to prevent re-renders)
const TypewriterDate = ({ date, isHovered }: { date: string, isHovered: boolean }) => {
    const [displayDate, setDisplayDate] = useState('')

    useEffect(() => {
        let i = 0
        setDisplayDate('')

        // Only animate if hovered or initial load (optional, user asked for hover trigger)
        // Actually user said: "relance uniquement sur la card que l'on hover"
        // implying it should run on hover.

        if (isHovered) {
            const interval = setInterval(() => {
                if (i < date.length) {
                    setDisplayDate(prev => prev + date.charAt(i))
                    i++
                } else {
                    clearInterval(interval)
                }
            }, 50)
            return () => clearInterval(interval)
        } else {
            // Reset or keep full date? 
            // If we want it to "replay" on hover, we reset when not hovered?
            // Or we just show full date when not hovered?
            // Let's default to showing the full date when NOT hovered, and animate from 0 when hovered?
            // Or animate ONCE on load, and Re-animate on hover?
            // User said: "relance... quand on hover". So re-play.
            setDisplayDate(date)
        }
    }, [date, isHovered])

    return <span>{displayDate}<span className={cn("animate-pulse", isHovered ? "opacity-100" : "opacity-0")}>_</span></span>
}

export default function BlogList() {
    const { unlock } = useGamification()
    const t = useTranslations('Blog')
    const [posts, setPosts] = useState<Post[]>([])
    const [search, setSearch] = useState('')
    const [selectedTag, setSelectedTag] = useState<string | null>(null)
    const [page, setPage] = useState(1)

    // Achievement States
    const [searchedTags, setSearchedTags] = useState<Set<string>>(new Set())
    const [isOracleMode, setIsOracleMode] = useState(false)
    const [placeholderIndex, setPlaceholderIndex] = useState(0)
    const oraclePlaceholders = [
        "Computing...",
        "The Answer is...",
        "Don't Panic...",
        "Life, Universe, Everything...",
        "42"
    ]

    // State for hover effect
    const [hoveredId, setHoveredId] = useState<string | null>(null)

    useEffect(() => {
        if (isOracleMode) {
            const interval = setInterval(() => {
                setPlaceholderIndex(prev => (prev + 1) % oraclePlaceholders.length)
            }, 2000)
            return () => clearInterval(interval)
        }
    }, [isOracleMode])

    // Handle Search Change (Oracle)
    const handleSearchChange = (value: string) => {
        setSearch(value)
        setPage(1)

        if (value.trim() === '42') {
            setIsOracleMode(true)
            unlock('THE_ORACLE')
        } else {
            setIsOracleMode(false)
        }
    }

    // Handle Tag Click (Topic Hunter)
    const handleTagClick = (tag: string | null) => {
        setSelectedTag(tag)
        setPage(1)

        if (tag) {
            const newTags = new Set(searchedTags)
            newTags.add(tag)
            setSearchedTags(newTags)

            if (newTags.size === 3) {
                unlock('TOPIC_HUNTER')
                confetti({
                    particleCount: 150,
                    spread: 80,
                    origin: { y: 0.6 },
                    colors: ['#FFD700', '#FFA500', '#ffffff'] // Gold theme
                })
            }
        }
    }

    const ITEMS_PER_PAGE = 6

    useEffect(() => {
        getPosts().then((dbPosts: any[]) => {
            const formatted = dbPosts
                .filter((p: any) => p.published)
                .sort((a: any, b: any) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
                .map((p: any) => ({
                    id: p.id,
                    slug: p.slug,
                    title: p.title,
                    excerpt: p.excerpt || 'No description',
                    date: p.createdAt,
                    readTime: p.readTime || '5 min read',
                    tags: p.tags && p.tags.length > 0 ? p.tags : ['Blog'],
                    coverImage: p.coverImage
                }))

            // DEBUG: Inject Mock Data if only 1 post (Featured) exists, to verify Grid
            if (formatted.length <= 1) {
                console.log("DEBUG: Injecting mocks for grid verification")
                formatted.push(
                    { id: 'mock-1', slug: 'mock-1', title: 'AI Ethics in 2025', excerpt: 'Exploring the moral landscape of AGI...', date: new Date().toISOString(), readTime: '5 min', tags: ['AI', 'Ethics'], coverImage: null },
                    { id: 'mock-2', slug: 'mock-2', title: 'React Server Components', excerpt: 'Deep dive into RSC architecture...', date: new Date().toISOString(), readTime: '8 min', tags: ['React', 'Web'], coverImage: null },
                    { id: 'mock-3', slug: 'mock-3', title: 'The Future of CSS', excerpt: 'From Tailwind to native nesting...', date: new Date().toISOString(), readTime: '4 min', tags: ['CSS', 'Design'], coverImage: null }
                )
            }

            setPosts(formatted)
        })
    }, [])

    // Filter Logic
    const filteredPosts = posts.filter(post => {
        const matchesSearch = post.title.toLowerCase().includes(search.toLowerCase()) ||
            (post.excerpt?.toLowerCase() || '').includes(search.toLowerCase())
        const matchesTag = selectedTag ? post.tags?.includes(selectedTag) : true
        return matchesSearch && matchesTag
    })

    // Debug Logs
    console.log('Posts:', posts.length, 'Filtered:', filteredPosts.length)

    // Pagination Logic
    const totalPages = Math.ceil(filteredPosts.length / ITEMS_PER_PAGE)
    const paginatedPosts = filteredPosts.slice((page - 1) * ITEMS_PER_PAGE, page * ITEMS_PER_PAGE)

    // Featured Article logic
    const showFeatured = !search && !selectedTag && page === 1 && posts.length > 0
    const featuredPost = showFeatured ? posts[0] : null
    const listPosts = showFeatured ? paginatedPosts.slice(1) : paginatedPosts

    return (
        <div className="space-y-12">
            {/* ... */}
            {/* Search & Filters */}
            <div className="space-y-6">
                <div className="relative max-w-lg mx-auto md:mx-0 group">
                    <Search className={cn(
                        "absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 transition-colors",
                        isOracleMode ? "text-green-500 animate-pulse" : "text-muted-foreground group-focus-within:text-primary"
                    )} />

                    {/* Matrix Glitch Overlay - Replaced Galaxy */}
                    <div className={cn(
                        "absolute inset-0 rounded-full border border-green-500/50 opacity-0 transition-opacity duration-300",
                        isOracleMode && "opacity-100 shadow-[0_0_10px_rgba(34,197,94,0.3)] bg-black"
                    )} />

                    <Input
                        placeholder={isOracleMode ? oraclePlaceholders[placeholderIndex] : t('search_placeholder')}
                        className={cn(
                            "pl-10 transition-all rounded-full h-11 border-white/10 relative z-10",
                            isOracleMode
                                ? "bg-black text-green-500 font-mono tracking-wider border-green-500/50 focus-visible:ring-green-500/50 placeholder:text-green-700"
                                : "bg-white/5 focus-visible:ring-primary/50 focus-visible:border-primary"
                        )}
                        value={search}
                        onChange={(e) => handleSearchChange(e.target.value)}
                    />
                    {isOracleMode && (
                        <div className="absolute right-12 top-1/2 -translate-y-1/2 text-[10px] font-mono text-green-700 animate-pulse pointer-events-none z-20">
                            SYSTEM_OVERRIDE
                        </div>
                    )}
                    {search && (
                        <button
                            onClick={() => handleSearchChange('')}
                            className="absolute right-3 top-1/2 -translate-y-1/2 p-1 hover:bg-white/10 rounded-full transition-colors"
                        >
                            <X className={cn("w-3 h-3", isOracleMode ? "text-green-500" : "text-muted-foreground")} />
                        </button>
                    )}
                </div>

                <div className="flex flex-wrap gap-2 items-center">
                    <span className="text-sm font-medium text-muted-foreground mr-2">{t('topics')}</span>
                    <motion.div className="flex flex-wrap gap-2">
                        <Badge
                            variant={selectedTag === null ? "default" : "secondary"}
                            className={`cursor-pointer transition-all hover:scale-105 ${selectedTag === null ? 'bg-primary text-primary-foreground' : 'bg-white/5 hover:bg-white/10'}`}
                            onClick={() => handleTagClick(null)}
                        >
                            {t('all')}
                        </Badge>
                        {Array.from(new Set(posts.flatMap(p => p.tags || []))).map(tag => {
                            // Don't show gold border if we've already hit the target
                            const isHunted = searchedTags.has(tag) && searchedTags.size < 3
                            return (
                                <Badge
                                    key={tag}
                                    variant={selectedTag === tag ? "default" : "secondary"}
                                    className={cn(
                                        "cursor-pointer transition-all hover:scale-105",
                                        selectedTag === tag ? 'bg-primary text-primary-foreground' : 'bg-white/5 hover:bg-white/10',
                                        isHunted && "border-yellow-500/50 shadow-[0_0_10px_rgba(234,179,8,0.2)] text-yellow-500"
                                    )}
                                    onClick={() => handleTagClick(tag)}
                                >
                                    {tag}
                                </Badge>
                            )
                        })}
                    </motion.div>
                </div>
            </div>

            <AnimatePresence>
                {/* Featured Post */}
                {featuredPost && (
                    <motion.div
                        initial={{ opacity: 0, y: 20, filter: "blur(10px)" }}
                        animate={{ opacity: 1, y: 0, filter: "blur(0px)" }}
                        exit={{ opacity: 0, y: -20 }}
                        transition={{ duration: 0.8, ease: "easeOut" }}
                        className="relative group cursor-pointer"
                    >
                        <Link href={featuredPost.id ? `/blog/entry/${featuredPost.slug}` : `/blog/${featuredPost.slug}`}>
                            <div className="relative aspect-[21/9] w-full overflow-hidden rounded-3xl bg-gradient-to-br from-neutral-900 to-slate-900 border border-white/10">
                                {!featuredPost.coverImage && (
                                    <div className="absolute inset-0 opacity-20 bg-[radial-gradient(ellipse_at_top_right,_var(--tw-gradient-stops))] from-cyan-900 via-transparent to-transparent" />
                                )}
                                {featuredPost.coverImage && (
                                    <Image
                                        src={featuredPost.coverImage}
                                        alt={featuredPost.title}
                                        fill
                                        className="object-cover transition-transform duration-700 group-hover:scale-105"
                                        priority
                                    />
                                )}
                                <div className="absolute inset-0 bg-gradient-to-t from-black/90 via-black/50 to-transparent p-8 md:p-12 flex flex-col justify-end items-start text-left">
                                    <div className="space-y-4 max-w-4xl">
                                        <div className="flex items-center gap-4 text-sm font-mono text-cyan-400">
                                            <span className="px-3 py-1 rounded-full bg-cyan-950/30 border border-cyan-500/30 backdrop-blur-md">
                                                {t('featured')}
                                            </span>
                                            <span>{typeof featuredPost.date === 'string' ? featuredPost.date : formatDistance(new Date(featuredPost.date!), new Date(), { addSuffix: true })}</span>
                                            <span>{featuredPost.readTime}</span>
                                        </div>
                                        <h2 className="text-4xl md:text-5xl font-bold text-white tracking-tight leading-tight group-hover:text-cyan-50 transition-colors">
                                            {featuredPost.title}
                                        </h2>
                                        <p className="text-lg text-gray-300 line-clamp-2 max-w-2xl">
                                            {featuredPost.excerpt}
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </Link>
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Grid */}
            <motion.div
                key="grid"
                layout
                className="grid gap-6 md:grid-cols-2 lg:grid-cols-3"
                onMouseLeave={() => setHoveredId(null)}
            >
                {listPosts.map((post) => (
                    <motion.article
                        key={post.slug}
                        initial={{ opacity: 1 }}
                        onMouseEnter={() => setHoveredId(post.id || post.slug)}
                        layout
                        className={cn(
                            "relative group flex flex-col h-full bg-white/5 border border-white/10 p-5 rounded-2xl transition-all duration-500",
                            hoveredId && hoveredId !== (post.id || post.slug) && "opacity-40 grayscale scale-95 blur-[1px]",
                            hoveredId && hoveredId === (post.id || post.slug) && "scale-[1.02] border-primary/30 bg-white/10 shadow-2xl shadow-black/50 z-10"
                        )}
                    >
                        {post.coverImage && (
                            <div className="aspect-[16/10] mb-5 rounded-xl overflow-hidden bg-black/20 relative">
                                <img
                                    src={post.coverImage}
                                    alt={post.title}
                                    className="object-cover w-full h-full transform group-hover:scale-105 transition-transform duration-500"
                                />
                            </div>
                        )}
                        <div className="flex-1 flex flex-col">
                            <div className="flex justify-between items-center mb-4 text-xs font-semibold uppercase tracking-wider text-muted-foreground/80 font-mono">
                                <TypewriterDate
                                    date={typeof post.date === 'string' ? post.date : formatDistance(new Date(post.date!), new Date(), { addSuffix: true })}
                                    isHovered={hoveredId === (post.id || post.slug)}
                                />
                                <span>{post.readTime}</span>
                            </div>

                            <h2 className="text-xl font-bold mb-3 leading-snug group-hover:text-primary transition-colors line-clamp-2">
                                <Link
                                    href={post.id ? `/blog/entry/${post.slug}` : `/blog/${post.slug}`}
                                    className="before:absolute before:inset-0"
                                >
                                    {post.title}
                                </Link>
                            </h2>

                            <p className="text-muted-foreground text-sm line-clamp-3 mb-6">
                                {post.excerpt}
                            </p>

                            <div className="flex flex-wrap gap-2 mt-auto pt-4 border-t border-white/5">
                                {post.tags?.slice(0, 3).map(tag => (
                                    <Badge key={tag} variant="secondary" className="bg-white/5 hover:bg-white/10 text-[10px] px-2 h-5 text-muted-foreground">
                                        {tag}
                                    </Badge>
                                ))}
                                {post.tags && post.tags.length > 3 && (
                                    <span className="text-[10px] text-muted-foreground self-center">+{post.tags.length - 3}</span>
                                )}
                            </div>
                        </div>
                    </motion.article>
                ))}
            </motion.div>

            {/* Pagination Controls */}
            {totalPages > 1 && (
                <div className="flex justify-center gap-2 mt-12">
                    <Button
                        variant="outline"
                        size="sm"
                        disabled={page === 1}
                        onClick={() => { setPage(p => p - 1); window.scrollTo({ top: 0, behavior: 'smooth' }) }}
                        className="rounded-full px-6"
                    >
                        {t('previous')}
                    </Button>
                    <div className="flex items-center gap-1 px-4">
                        {Array.from({ length: totalPages }).map((_, i) => (
                            <div
                                key={i}
                                className={`w-2 h-2 rounded-full transition-all ${page === i + 1 ? 'bg-primary scale-125' : 'bg-white/20'}`}
                            />
                        ))}
                    </div>
                    <Button
                        variant="outline"
                        size="sm"
                        disabled={page === totalPages}
                        onClick={() => { setPage(p => p + 1); window.scrollTo({ top: 0, behavior: 'smooth' }) }}
                        className="rounded-full px-6"
                    >
                        {t('next')}
                    </Button>
                </div>
            )}
        </div>
    )
}

'use client'

import { ReactFlow, Background, Controls } from '@xyflow/react'
import '@xyflow/react/dist/style.css'
import { useEffect, useState, useMemo } from 'react'
import CustomNode from '@/components/editor/extensions/CustomNode'

interface ClientFlowRendererProps {
    nodes: any[]
    edges: any[]
}

export default function ClientFlowRenderer({ nodes: initialNodes, edges: initialEdges }: ClientFlowRendererProps) {
    // We need to ensure this only renders on client to avoid hydration mismatch
    const [isMounted, setIsMounted] = useState(false)
    const nodeTypes = useMemo(() => ({ custom: CustomNode }), [])

    useEffect(() => {
        setIsMounted(true)
    }, [])

    if (!isMounted) return <div className="h-[400px] w-full bg-muted/20 animate-pulse rounded-lg" />

    // Read-only view
    return (
        <div className="h-[400px] w-full border rounded-lg bg-background/50 backdrop-blur-sm overflow-hidden my-8">
            <ReactFlow
                nodes={initialNodes}
                edges={initialEdges}
                nodeTypes={nodeTypes}
                fitView
                proOptions={{ hideAttribution: true }}
                nodesDraggable={false}
                nodesConnectable={false}
                elementsSelectable={false}
            >
                <Background color="#555" gap={16} />
                <Controls showInteractive={false} />
            </ReactFlow>
        </div>
    )
}

'use client'

import { useForm } from "react-hook-form"
import { zodResolver } from "@hookform/resolvers/zod"
import { contactSchema, ContactFormValues } from "@/lib/schemas"
import { useState } from "react"
import { toast } from "sonner"
import { motion } from "framer-motion"
import { Loader2, Send } from "lucide-react"
import { SpotlightInput, SpotlightTextarea } from "@/components/ui/spotlight-input"
import MagneticButton from "@/components/ui/magnetic-button"

import { useTranslations } from "next-intl"

export function ContactForm() {
    const t = useTranslations('Contact')
    const [isSubmitting, setIsSubmitting] = useState(false)

    const form = useForm<ContactFormValues>({
        resolver: zodResolver(contactSchema),
        defaultValues: {
            name: "",
            email: "",
            subject: "",
            message: "",
        },
    })

    async function onSubmit(data: ContactFormValues) {
        setIsSubmitting(true)
        try {
            const response = await fetch("/api/send", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(data),
            })

            if (!response.ok) throw new Error("Failed to send message")

            toast.success(t('toast_success_title'), {
                description: t('toast_success_desc'),
            })
            form.reset()
        } catch (error) {
            toast.error(t('toast_error_title'), {
                description: t('toast_error_desc'),
            })
        } finally {
            setIsSubmitting(false)
        }
    }

    return (
        <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="w-full max-w-xl mx-auto p-8 rounded-2xl bg-white/5 border border-white/10 backdrop-blur-md shadow-2xl"
        >
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                        <label htmlFor="name" className="text-sm font-medium text-white/70 uppercase tracking-widest">{t('form_name_label')}</label>
                        <SpotlightInput
                            {...form.register("name")}
                            placeholder={t('form_name_placeholder')}
                        />
                        {form.formState.errors.name && (
                            <p className="text-red-400 text-xs mt-1">{form.formState.errors.name.message}</p>
                        )}
                    </div>

                    <div className="space-y-2">
                        <label htmlFor="email" className="text-sm font-medium text-white/70 uppercase tracking-widest">{t('form_email_label')}</label>
                        <SpotlightInput
                            {...form.register("email")}
                            placeholder={t('form_email_placeholder')}
                        />
                        {form.formState.errors.email && (
                            <p className="text-red-400 text-xs mt-1">{form.formState.errors.email.message}</p>
                        )}
                    </div>
                </div>

                <div className="space-y-2">
                    <label htmlFor="subject" className="text-sm font-medium text-white/70 uppercase tracking-widest">{t('form_subject_label')}</label>
                    <SpotlightInput
                        {...form.register("subject")}
                        placeholder={t('form_subject_placeholder')}
                    />
                    {form.formState.errors.subject && (
                        <p className="text-red-400 text-xs mt-1">{form.formState.errors.subject.message}</p>
                    )}
                </div>

                <div className="space-y-2">
                    <label htmlFor="message" className="text-sm font-medium text-white/70 uppercase tracking-widest">{t('form_message_label')}</label>
                    <SpotlightTextarea
                        {...form.register("message")}
                        placeholder={t('form_message_placeholder')}
                    />
                    {form.formState.errors.message && (
                        <p className="text-red-400 text-xs mt-1">{form.formState.errors.message.message}</p>
                    )}
                </div>

                <MagneticButton
                    type="submit"
                    variant="primary"
                    disabled={isSubmitting}
                    className="w-full"
                >
                    {isSubmitting ? (
                        <Loader2 className="w-5 h-5 animate-spin" />
                    ) : (
                        <div className="flex items-center gap-2">
                            {t('form_submit_button')}
                            <Send className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
                        </div>
                    )}
                </MagneticButton>
            </form>
        </motion.div>
    )
}

'use client'

import React, { useRef, useState } from 'react'
import { motion, useMotionValue, useSpring, useTransform } from 'framer-motion'
import { Mail, MapPin, CheckCircle2, Copy } from 'lucide-react'
import { cn } from '@/lib/utils'
import { toast } from 'sonner'

export function ContactTiltCard() {
    const ref = useRef<HTMLDivElement>(null)
    const [copied, setCopied] = useState(false)

    // Motion values for tilt effect
    const x = useMotionValue(0)
    const y = useMotionValue(0)

    // Spring physics for smooth return
    const mouseX = useSpring(x, { stiffness: 500, damping: 100 })
    const mouseY = useSpring(y, { stiffness: 500, damping: 100 })

    function handleMouseMove({ currentTarget, clientX, clientY }: React.MouseEvent) {
        const { left, top, width, height } = currentTarget.getBoundingClientRect()
        const xPct = (clientX - left) / width - 0.5
        const yPct = (clientY - top) / height - 0.5

        // Tilt intensity
        x.set(xPct)
        y.set(yPct)
    }

    function handleMouseLeave() {
        x.set(0)
        y.set(0)
    }

    // Transforms
    const rotateX = useTransform(mouseY, [-0.5, 0.5], [15, -15]) // Reverse for natural tilt
    const rotateY = useTransform(mouseX, [-0.5, 0.5], [-15, 15])
    const glareX = useTransform(mouseX, [-0.5, 0.5], [0, 100])
    const glareY = useTransform(mouseY, [-0.5, 0.5], [0, 100])

    // Copy Action
    const handleCopy = () => {
        navigator.clipboard.writeText("contact@roketag.com")
        setCopied(true)
        toast.success("Email copied to clipboard!")
        setTimeout(() => setCopied(false), 2000)
    }

    return (
        <motion.div
            ref={ref}
            style={{
                perspective: 1000,
            }}
            className="relative w-full max-w-md mx-auto aspect-[1.7/1] sm:aspect-[1.8/1]"
            onMouseMove={handleMouseMove}
            onMouseLeave={handleMouseLeave}
        >
            <motion.div
                style={{
                    rotateX,
                    rotateY,
                    transformStyle: "preserve-3d",
                }}
                className="relative w-full h-full rounded-2xl bg-gradient-to-br from-white/10 to-white/5 border border-white/20 backdrop-blur-xl shadow-2xl overflow-hidden group"
            >
                {/* Holographic Glare */}
                <motion.div
                    style={{
                        background: `radial-gradient(circle at ${glareX}% ${glareY}%, rgba(255,255,255,0.3) 0%, transparent 60%)`,
                        opacity: useTransform(mouseX, [-0.5, 0.5], [0.3, 0.6])
                    }}
                    className="absolute inset-0 z-20 pointer-events-none mix-blend-overlay"
                />

                {/* Content Layer (Float Effect) */}
                <div
                    className="absolute inset-0 z-30 flex flex-col justify-between p-6 sm:p-8"
                    style={{ transform: "translateZ(50px)" }} // 3D Float
                >
                    {/* Header */}
                    <div className="flex justify-between items-start">
                        <div className="flex items-center gap-3">
                            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-primary to-blue-500 flex items-center justify-center shadow-lg shadow-primary/25">
                                <span className="font-bold text-white text-lg">A</span>
                            </div>
                            <div>
                                <h3 className="font-bold text-white leading-none">Alexandre S.</h3>
                                <p className="text-xs text-white/50 font-mono mt-1">Software Engineer</p>
                            </div>
                        </div>

                        {/* Status Badge */}
                        <div className="px-3 py-1 rounded-full bg-green-500/20 border border-green-500/30 flex items-center gap-2 backdrop-blur-md">
                            <div className="w-1.5 h-1.5 rounded-full bg-green-500 animate-pulse" />
                            <span className="text-[10px] font-bold text-green-400 uppercase tracking-wider">Available</span>
                        </div>
                    </div>

                    {/* Middle: Chip Visual (Decorative) */}
                    <div className="absolute top-1/2 right-8 -translate-y-1/2 opacity-20 hidden sm:block">
                        <svg width="60" height="60" viewBox="0 0 24 24" fill="none" stroke="currentColor" className="text-white">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5" />
                        </svg>
                    </div>

                    {/* Footer: Contacts */}
                    <div className="space-y-4">
                        {/* Email */}
                        <button
                            onClick={handleCopy}
                            className="w-full flex items-center justify-between p-3 rounded-xl bg-white/5 border border-white/10 hover:bg-white/10 hover:border-white/30 transition-all group/btn text-left"
                        >
                            <div className="flex items-center gap-3">
                                <div className="p-2 rounded-lg bg-neutral-900 border border-white/10">
                                    <Mail className="w-4 h-4 text-primary" />
                                </div>
                                <div className="flex flex-col">
                                    <span className="text-[10px] text-white/40 uppercase tracking-widest">Email</span>
                                    <span className="text-sm font-medium text-white group-hover/btn:text-primary transition-colors">contact@roketag.com</span>
                                </div>
                            </div>
                            {copied ? <CheckCircle2 className="w-4 h-4 text-green-500" /> : <Copy className="w-4 h-4 text-white/20 group-hover/btn:text-white" />}
                        </button>

                        {/* Location */}
                        <div className="flex items-center gap-3 px-3">
                            <MapPin className="w-4 h-4 text-white/40" />
                            <span className="text-xs text-white/60 font-mono">Bordeaux, France (Remote-ready)</span>
                        </div>
                    </div>
                </div>

                {/* Background Noise/Texture */}
                <div className="absolute inset-0 opacity-20 bg-[url('https://grainy-gradients.vercel.app/noise.svg')] mix-blend-overlay z-10" />
            </motion.div>
        </motion.div>
    )
}

'use client'
import { useState, useEffect, useCallback, useRef } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Button } from '@/components/ui/button'
import { Trophy, RefreshCcw, X, Gamepad2 } from 'lucide-react'
import { useGamification } from '@/context/GamificationContext'

const GRID_SIZE = 20
const CELL_SIZE = 25 // Slightly larger for fullscreen feel
const SPEED = 100

type Point = { x: number, y: number }

// ... imports

interface SnakeGameProps {
    fullScreen?: boolean
}

export default function SnakeGame({ fullScreen = false }: SnakeGameProps) {
    const { unlock } = useGamification()
    const canvasRef = useRef<HTMLCanvasElement>(null)

    // Game State
    const [isArcadeOpen, setIsArcadeOpen] = useState(fullScreen) // Default open if fullScreen

    const [snake, setSnake] = useState<Point[]>([{ x: 10, y: 10 }])
    const [food, setFood] = useState<Point>({ x: 15, y: 15 })
    const [direction, setDirection] = useState<Point>({ x: 1, y: 0 })
    const [gameOver, setGameOver] = useState(false)
    const [score, setScore] = useState(0)
    const [isPlaying, setIsPlaying] = useState(false)
    const [highScore, setHighScore] = useState(0)

    // Init High Score
    useEffect(() => {
        const saved = localStorage.getItem('snake_highscore')
        if (saved) setHighScore(parseInt(saved))

        // Auto-start if fullscreen
        if (fullScreen) {
            setIsPlaying(true)
            setIsArcadeOpen(true)
            // Need to wait for canvas ref? loop handles logic.
        }
    }, [fullScreen])

    // Achievement Check
    useEffect(() => {
        if (score >= 10) unlock('SNAKE_MASTER')
    }, [score, unlock])

    const generateFood = useCallback(() => {
        return {
            x: Math.floor(Math.random() * GRID_SIZE),
            y: Math.floor(Math.random() * GRID_SIZE)
        }
    }, [])

    const resetGame = () => {
        setSnake([{ x: 10, y: 10 }])
        setFood(generateFood())
        setDirection({ x: 1, y: 0 })
        setGameOver(false)
        setScore(0)
        setIsPlaying(true)
    }

    // Game Loop
    useEffect(() => {
        // Run if playing AND (arcade is open OR fullscreen)
        const active = isPlaying && (isArcadeOpen || fullScreen)
        if (!active || gameOver) return

        const moveSnake = () => {
            setSnake(prev => {
                const newHead = {
                    x: (prev[0].x + direction.x + GRID_SIZE) % GRID_SIZE,
                    y: (prev[0].y + direction.y + GRID_SIZE) % GRID_SIZE
                }

                // Check collision with self
                if (prev.some(segment => segment.x === newHead.x && segment.y === newHead.y)) {
                    setGameOver(true)
                    if (score > highScore) {
                        setHighScore(score)
                        localStorage.setItem('snake_highscore', score.toString())
                    }
                    return prev
                }

                const newSnake = [newHead, ...prev]

                // Check food
                if (newHead.x === food.x && newHead.y === food.y) {
                    setScore(s => s + 1)
                    setFood(generateFood())
                } else {
                    newSnake.pop()
                }

                return newSnake
            })
        }

        const interval = setInterval(moveSnake, SPEED)
        return () => clearInterval(interval)
    }, [isPlaying, gameOver, direction, food, score, highScore, generateFood, isArcadeOpen, fullScreen])

    // Controls
    useEffect(() => {
        const active = isArcadeOpen || fullScreen
        if (!active) return

        const handleKey = (e: KeyboardEvent) => {
            // Prevent defaults
            if (['ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight', ' '].includes(e.key)) {
                e.preventDefault()
            }

            // Close on Escape ONLY if NOT fullScreen (standalone page handles nav)
            if (e.key === 'Escape' && !fullScreen) {
                setIsArcadeOpen(false)
                setIsPlaying(false)
            }

            switch (e.key) {
                case 'ArrowUp': if (direction.y === 0) setDirection({ x: 0, y: -1 }); break;
                case 'ArrowDown': if (direction.y === 0) setDirection({ x: 0, y: 1 }); break;
                case 'ArrowLeft': if (direction.x === 0) setDirection({ x: -1, y: 0 }); break;
                case 'ArrowRight': if (direction.x === 0) setDirection({ x: 1, y: 0 }); break;
            }
        }
        window.addEventListener('keydown', handleKey)
        return () => window.removeEventListener('keydown', handleKey)
    }, [direction, isArcadeOpen, fullScreen])

    // Draw Canvas
    useEffect(() => {
        const active = isArcadeOpen || fullScreen
        if (!active) return

        const ctx = canvasRef.current?.getContext('2d')
        if (!ctx) return

        // Retro Background
        ctx.fillStyle = '#111'
        ctx.fillRect(0, 0, GRID_SIZE * CELL_SIZE, GRID_SIZE * CELL_SIZE)

        // Grid lines (subtle)
        ctx.strokeStyle = '#222'
        ctx.lineWidth = 1
        for (let i = 0; i <= GRID_SIZE; i++) {
            ctx.beginPath()
            ctx.moveTo(i * CELL_SIZE, 0)
            ctx.lineTo(i * CELL_SIZE, GRID_SIZE * CELL_SIZE)
            ctx.stroke()
            ctx.beginPath()
            ctx.moveTo(0, i * CELL_SIZE)
            ctx.lineTo(GRID_SIZE * CELL_SIZE, i * CELL_SIZE)
            ctx.stroke()
        }

        // Food (Retro Pixel Apple style)
        ctx.fillStyle = '#ef4444'
        ctx.fillRect(food.x * CELL_SIZE + 2, food.y * CELL_SIZE + 2, CELL_SIZE - 4, CELL_SIZE - 4)

        // Snake
        snake.forEach((segment, i) => {
            ctx.fillStyle = i === 0 ? '#4ade80' : '#22c55e'
            ctx.fillRect(segment.x * CELL_SIZE + 1, segment.y * CELL_SIZE + 1, CELL_SIZE - 2, CELL_SIZE - 2)
        })

    }, [snake, food, isArcadeOpen, fullScreen])

    const openArcade = () => {
        setIsArcadeOpen(true)
        resetGame()
    }

    const GameInterface = (
        <div className="flex flex-col items-center gap-8 max-w-lg w-full">
            {/* Arcade Header */}
            <div className="flex w-full justify-between items-end border-b border-white/10 pb-4">
                <div>
                    <h2 className="text-4xl font-black italic text-transparent bg-clip-text bg-gradient-to-r from-primary to-purple-500">ARCADE MODE</h2>
                    <p className="text-xs font-mono text-muted-foreground mt-1">Use Arrow Keys to Move â€¢ ESC to Quit</p>
                </div>
                <div className="text-right">
                    <p className="text-xs font-mono text-muted-foreground">SCORE</p>
                    <p className="text-4xl font-mono font-bold text-white tabular-nums">{score.toString().padStart(3, '0')}</p>
                </div>
            </div>

            {/* Game Area */}
            <div className="relative p-2 bg-[#222] rounded-xl shadow-2xl ring-4 ring-white/10">
                <canvas
                    ref={canvasRef}
                    width={GRID_SIZE * CELL_SIZE}
                    height={GRID_SIZE * CELL_SIZE}
                    className="rounded-lg bg-black"
                />

                {/* Game Over Screen */}
                {gameOver && (
                    <div className="absolute inset-0 z-10 flex flex-col items-center justify-center bg-black/80 backdrop-blur-sm rounded-lg">
                        <p className="text-red-500 text-5xl font-black italic mb-2 tracking-tighter">GAME OVER</p>
                        <p className="text-white mb-8 font-mono">Score: {score}</p>
                        <Button onClick={resetGame} size="lg" className="h-14 px-8 text-xl font-bold uppercase tracking-widest hover:scale-105 transition-transform">
                            Retry
                        </Button>
                    </div>
                )}
            </div>
        </div>
    )

    if (fullScreen) {
        return (
            <div className="w-full h-full flex items-center justify-center p-6">
                {GameInterface}
            </div>
        )
    }

    return (
        <>
            {/* Preview Card */}
            <div
                onClick={openArcade}
                className="cursor-pointer group relative flex flex-col items-center justify-center p-12 border border-white/10 rounded-3xl bg-white/5 hover:bg-white/10 transition-all hover:scale-[1.02] border-dashed"
            >
                <Gamepad2 size={48} className="mb-4 text-muted-foreground group-hover:text-primary transition-colors" />
                <h3 className="text-2xl font-black uppercase tracking-widest mb-2">Retro Snake</h3>
                <p className="text-sm font-mono text-muted-foreground mb-6">High Score: {highScore}</p>
                <Button className="pointer-events-none uppercase tracking-widest font-bold">
                    Insert Coin / Click to Play
                </Button>
            </div>

            {/* Arcade Overlay (Portal-like) */}
            <AnimatePresence>
                {isArcadeOpen && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="fixed inset-0 z-[9999] bg-black/90 backdrop-blur-xl flex items-center justify-center p-6"
                    >
                        {/* Close Button */}
                        <button
                            onClick={() => setIsArcadeOpen(false)}
                            className="absolute top-8 right-8 text-white/50 hover:text-white transition-colors"
                        >
                            <X size={32} />
                            <span className="sr-only">Close Arcade</span>
                        </button>

                        {GameInterface}
                    </motion.div>
                )}
            </AnimatePresence>
        </>
    )
}

'use client';

import { motion } from 'framer-motion';
import { ArrowUpRight } from 'lucide-react';
import { Link } from '@/i18n/routingConfig';
import { Project } from './types';
import { useGamification } from '@/context/GamificationContext';
import { useState, useEffect } from 'react';
import { cn } from '@/lib/utils';

interface LabListProps {
    projects: Project[];
    setHoveredProject: (project: Project | null) => void;
}

export default function LabList({ projects, setHoveredProject }: LabListProps) {
    const { unlock, unlocked } = useGamification()

    // Check for LAB_MASTER
    useEffect(() => {
        if (
            unlocked.includes('MAD_SCIENTIST') &&
            unlocked.includes('SYSTEM_MELTDOWN') &&
            unlocked.includes('THE_ARCHIVIST')
        ) {
            unlock('LAB_MASTER')
        }
    }, [unlocked, unlock])

    // --- MAD SCIENTIST STATE ---
    const [viewedProjects, setViewedProjects] = useState<Set<string>>(new Set())

    // --- SYSTEM MELTDOWN STATE ---
    const [titleClicks, setTitleClicks] = useState(0)
    const [glitchText, setGlitchText] = useState("Experiments_")

    // --- THE ARCHIVIST STATE ---
    const [isClassified, setIsClassified] = useState(false)

    // Handle Project Hover
    const handleProjectHover = (project: Project) => {
        setHoveredProject(project)

        if (!viewedProjects.has(project.id)) {
            const newSet = new Set(viewedProjects)
            newSet.add(project.id)
            setViewedProjects(newSet)

            if (newSet.size === projects.length) {
                unlock('MAD_SCIENTIST')
            }
        }
    }

    // Handle Title Click (Meltdown)
    const handleTitleClick = () => {
        const newClicks = titleClicks + 1
        setTitleClicks(newClicks)

        // Glitch effect logic
        const chars = "!@#$%^&*()_+-=[]{}|;':,./<>?"
        const original = "Experiments_"

        if (newClicks < 5) {
            // Light glitch
            const glitched = original.split('').map((char, _) => {
                if (Math.random() < (newClicks * 0.1)) {
                    return chars[Math.floor(Math.random() * chars.length)]
                }
                return char
            }).join('')
            setGlitchText(glitched)
        } else if (newClicks === 5) {
            // CRASH
            unlock('SYSTEM_MELTDOWN')
            setGlitchText("S_Y_S_T_E_M___F_A_I_L_U_R_E")
            document.body.classList.add('animate-shake') // Assumes global shake utility or we add inline style
            setTimeout(() => {
                setGlitchText(original)
                setTitleClicks(0)
                document.body.classList.remove('animate-shake')
            }, 2000)
        }
    }

    // Handle Archive Double Click (Archivist)
    const handleArchiveDbClick = () => {
        if (!isClassified) {
            setIsClassified(true)
            unlock('THE_ARCHIVIST')
        }
    }

    const containerVariants = {
        hidden: { opacity: 0 },
        visible: {
            opacity: 1,
            transition: {
                staggerChildren: 0.1,
                delayChildren: 0.3
            }
        }
    }

    const itemVariants = {
        hidden: { opacity: 0, x: -20, filter: 'blur(10px)' },
        visible: {
            opacity: 1,
            x: 0,
            filter: 'blur(0px)',
            transition: { duration: 0.5, ease: "easeOut" as const }
        }
    }

    return (
        <div className="relative z-10 flex flex-col container mx-auto px-6 pt-32 pb-24">
            <div className="mb-12 relative flex flex-col">
                <div className="flex items-center justify-between">
                    <motion.span
                        initial={{ opacity: 0, y: -10 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.2 }}
                        onDoubleClick={handleArchiveDbClick}
                        className={cn(
                            "font-mono text-xs uppercase tracking-widest block mb-2 cursor-pointer select-none transition-colors",
                            isClassified ? "text-red-500 animate-pulse font-bold" : "text-neutral-500 hover:text-neutral-300"
                        )}
                    >
                        {isClassified ? "âš ï¸ TOP SECRET // CLASSIFIED FILES" : "The Archive"}
                    </motion.span>

                    {isClassified && (
                        <motion.button
                            initial={{ opacity: 0, x: 20 }}
                            animate={{ opacity: 1, x: 0 }}
                            onClick={() => setIsClassified(false)}
                            className="text-xs font-mono bg-red-500/10 text-red-500 border border-red-500/20 px-3 py-1 rounded hover:bg-red-500 hover:text-black transition-colors"
                        >
                            DECLASSIFY
                        </motion.button>
                    )}
                </div>

                {/* Ghost Blur Title Effect */}
                <motion.div
                    className="relative inline-block"
                >
                    <motion.h1
                        onClick={handleTitleClick}
                        initial={{ opacity: 0, filter: 'blur(20px)', scale: 1.1 }}
                        animate={{ opacity: 1, filter: 'blur(0px)', scale: 1 }}
                        transition={{ duration: 1, ease: "circOut" }}
                        className={cn(
                            "text-4xl md:text-5xl font-bold text-white tracking-tight cursor-default select-none transition-colors relative z-10",
                            titleClicks > 0 && "text-red-500/80 font-mono"
                        )}
                    >
                        {glitchText}
                    </motion.h1>

                    {/* Ghost Layer */}
                    <motion.h1
                        initial={{ opacity: 0, x: 10, filter: 'blur(10px)' }}
                        animate={{ opacity: [0, 0.4, 0], x: [10, 0, -5], filter: ['blur(10px)', 'blur(2px)', 'blur(10px)'] }}
                        transition={{ duration: 1.5, ease: "easeInOut", delay: 0.1 }}
                        className={cn(
                            "absolute top-0 left-0 text-4xl md:text-5xl font-bold text-indigo-500/50 tracking-tight cursor-default select-none pointer-events-none mix-blend-screen",
                        )}
                    >
                        {glitchText}
                    </motion.h1>
                </motion.div>
            </div>

            <motion.div
                variants={containerVariants}
                initial="hidden"
                animate="visible"
                className="flex flex-col"
            >
                <motion.div
                    variants={{ hidden: { opacity: 0 }, visible: { opacity: 1 } }}
                    className="grid grid-cols-12 gap-4 pb-4 border-b border-white/10 text-xs font-mono text-neutral-500 uppercase tracking-wider mb-2"
                >
                    <div className="col-span-1">Idx</div>
                    <div className="col-span-5 md:col-span-6">Project</div>
                    <div className="col-span-3 md:col-span-2 text-right">Tech</div>
                    <div className="col-span-3 text-right">Year</div>
                </motion.div>

                {projects.map((project, index) => (
                    <Link
                        key={project.id}
                        href={project.link}
                        onMouseEnter={() => handleProjectHover(project)}
                        onMouseLeave={() => setHoveredProject(null)}
                        className="group relative block"
                    >
                        {/* Hover Gradient Scanline */}
                        <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/[0.03] to-transparent -translate-x-full group-hover:translate-x-full transition-transform duration-1000 ease-in-out pointer-events-none" />

                        <motion.div
                            variants={itemVariants}
                            className={cn(
                                "grid grid-cols-12 gap-4 py-8 border-b items-center transition-colors relative",
                                isClassified ? "border-red-500/20 bg-red-950/10" : "border-white/5 group-hover:border-white/20"
                            )}
                        >
                            <div className={cn("col-span-1 font-mono text-sm transition-colors", isClassified ? "text-red-500" : "text-neutral-600 group-hover:text-white")}>
                                {isClassified ? ((index + 13) * 7919).toString(16).slice(-4).toUpperCase() : String(index + 1).padStart(2, '0')}
                            </div>
                            <div className="col-span-5 md:col-span-6">
                                <h3 className={cn(
                                    "text-2xl md:text-4xl font-light tracking-tight flex items-center gap-4 transition-colors",
                                    isClassified ? "text-red-400 font-mono" : "text-neutral-300 group-hover:text-white"
                                )}>
                                    {isClassified ? "REDACTED" : project.title}
                                    {!isClassified && <ArrowUpRight className="w-5 h-5 opacity-0 -translate-x-2 group-hover:opacity-100 group-hover:translate-x-0 transition-all duration-300 text-neutral-400" />}
                                </h3>
                                {/* Mobile Description */}
                                <div className="block md:hidden mt-2">
                                    <p className="text-sm text-neutral-400 font-light leading-relaxed">
                                        {project.description}
                                    </p>
                                </div>
                            </div>
                            <div className="col-span-3 md:col-span-2 text-right hidden md:block">
                                <div className="flex flex-col items-end gap-1">
                                    {project.tech.map((t) => (
                                        <span key={t} className={cn(
                                            "text-xs font-mono px-2 py-1 rounded-sm border transition-colors",
                                            isClassified ? "bg-red-900/20 text-red-500 border-red-500/20" : "text-neutral-500 bg-neutral-900/50 border-white/5 group-hover:border-white/10"
                                        )}>
                                            {isClassified ? "ERR" : t}
                                        </span>
                                    ))}
                                </div>
                            </div>
                            <div className="col-span-3 text-right font-mono text-sm transition-colors text-neutral-500 group-hover:text-white">
                                {isClassified ? "Unknown" : project.year}
                            </div>
                        </motion.div>
                    </Link>
                ))}
            </motion.div>
        </div >
    );
}

'use client'

import { useEffect, useState } from 'react'
import { createPortal } from 'react-dom'

export default function ClientPortal({ children }: { children: React.ReactNode }) {
    const [mounted, setMounted] = useState(false)

    useEffect(() => {
        setMounted(true)
        // Cleanup function not needed for simple body portal usually, 
        // but explicit removal is handled by React Portal.
    }, [])

    if (!mounted) return null

    return createPortal(children, document.body)
}
